{
  "task": "Comprehensive Review of Nigeria",
  "objective": "Produce a well‑researched, balanced, and engaging review covering Nigeria's geography, history, politics, economy, culture, tourism, challenges, and future prospects.",
  "agents": {
    "ResearchAgent": {
      "role": "Gather and synthesize reliable information",
      "steps": [
        {
          "id": "R1",
          "description": "Define sub‑sections for the review (e.g., Overview, History, Politics, Economy, Society & Culture, Tourism, Current Challenges, Outlook).",
          "output": "Document outlining the review structure with brief bullet points for each section."
        },
        {
          "id": "R2",
          "description": "Collect up‑to‑date data from credible sources (World Bank, IMF, UNDP, scholarly articles, reputable news outlets, government portals, and cultural institutions).",
          "output": "Annotated bibliography with URLs, publication dates, and relevance notes."
        },
        {
          "id": "R3",
          "description": "Extract key statistics and facts for each sub‑section (population, GDP, HDI, major industries, linguistic groups, etc.).",
          "output": "Spreadsheet of quantitative data and a summary of qualitative insights."
        },
        {
          "id": "R4",
          "description": "Identify recent developments (e.g., 2024 elections, economic reforms, infrastructure projects, cultural events).",
          "output": "Timeline of major events from the past 5 years."
        },
        {
          "id": "R5",
          "description": "Highlight contrasting viewpoints on contentious topics (e.g., security challenges, oil dependency, federalism).",
          "output": "Balanced perspective matrix noting source bias and key arguments."
        }
      ],
      "deliverables": [
        "Review_structure.docx",
        "Nigeria_research_bibliography.json",
        "Nigeria_key_statistics.xlsx",
        "Recent_developments_timeline.csv",
        "Balanced_perspectives_matrix.xlsx"
      ],
      "estimated_time_hours": 6
    },
    "WriterAgent": {
      "role": "Draft the full review using research outputs",
      "steps": [
        {
          "id": "W1",
          "description": "Create an outline based on the ResearchAgent’s structure document.",
          "output": "Detailed outline with word count targets per section."
        },
        {
          "id": "W2",
          "description": "Write each section, integrating statistics, quotes, and citations.",
          "output": "Draft_review_Nigeria.docx (complete manuscript)."
        },
        {
          "id": "W3",
          "description": "Ensure a neutral, informative tone while making the text engaging (use anecdotes, notable figures, and vivid descriptions).",
          "output": "Annotated draft highlighting tone checks."
        },
        {
          "id": "W4",
          "description": "Compile a reference list in APA style and insert in‑text citations.",
          "output": "References.bib"
        },
        {
          "id": "W5",
          "description": "Add visual aids (charts, tables, maps) using the data spreadsheets.",
          "output": "Embedded figures in Draft_review_Nigeria.docx and source files (e.g., .png)."
        }
      ],
      "deliverables": [
        "Draft_review_Nigeria.docx",
        "References.bib",
        "Figures.zip"
      ],
      "estimated_time_hours": 5
    },
    "ReviewerAgent": {
      "role": "Critically evaluate the draft for accuracy, completeness, bias, style, and formatting",
      "steps": [
        {
          "id": "RV1",
          "description": "Cross‑verify every factual claim against the ResearchAgent’s bibliography and data tables.",
          "output": "Fact‑check spreadsheet with pass/fail status."
        },
        {
          "id": "RV2",
          "description": "Assess balance of perspectives, ensuring no single viewpoint dominates.",
          "output": "Bias‑assessment report (scale 1‑5)."
        },
        {
          "id": "RV3",
          "description": "Check adherence to the outline, word‑count targets, and logical flow.",
          "output": "Structural review notes."
        },
        {
          "id": "RV4",
          "description": "Edit for grammar, style consistency, and compliance with the chosen citation format.",
          "output": "Edited version of the manuscript (Review_Nigeria_Final.docx)."
        },
        {
          "id": "RV5",
          "description": "Validate all visual aids for clarity, correct labeling, and source attribution.",
          "output": "Figure‑validation checklist."
        }
      ],
      "deliverables": [
        "Fact_check_report.xlsx",
        "Bias_assessment.pdf",
        "Structural_review_notes.txt",
        "Review_Nigeria_Final.docx",
        "Figure_validation_checklist.xlsx"
      ],
      "estimated_time_hours": 4
    }
  },
  "overall_timeline": {
    "Day 1": [
      "ResearchAgent – R1, R2",
      "WriterAgent – W1"
    ],
    "Day 2": [
      "ResearchAgent – R3, R4, R5",
      "WriterAgent – W2"
    ],
    "Day 3": [
      "WriterAgent – W3, W4, W5",
      "ReviewerAgent – RV1, RV2"
    ],
    "Day 4": [
      "ReviewerAgent – RV3, RV4, RV5"
    ],
    "Day 5": [
      "Final QA and packaging of all deliverables"
    ]
  },
  "total_estimated_hours": 15,
  "notes": "If any data source is behind a paywall or unavailable, flag it early (R2) so alternative open‑access sources can be sourced. All citations must be verifiable, and any contentious claim should be accompanied by at least two independent references."
}
